import React, { useState } from 'react';
import '../InstituteDashboard.css';
import logo from '../images/GCC-TBC.png'; // Import the logo image
import LoginForm from './StudentForm';
import StudentList from './StudentList';
import PayStudentList from './PayStudentList';
import Dashboard from './Dashboard'; // Import the Dashboard component
import { Link, Route, Routes } from 'react-router-dom';

const InstituteDashboard = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
<nav className="navbar">
  <div className="logo-and-menu">
    <div className="logo">
      <img src={logo} alt="logo" />
      <div className="text-normal" style={{ whiteSpace: 'nowrap', fontSize: '0.9rem', display: 'block', alignItems: 'center', fontWeight: 'bold' }}>
        <span>MSCE PUNE COMPUTER SHORTHAND DEMO</span>
        <div className="institute-info">
          15001 ABC Computer Typing Institute
        </div>
      </div>
    </div>
    <button className="menu-toggle" onClick={toggleSidebar}>
      â˜°
    </button>
  </div>
  <ul className={`menu-links ${isSidebarOpen ? 'open' : ''}`}>
    <li className="nav-link"><Link to="/dashboard"><span>Dashboard</span></Link></li>
    <li className="nav-link"><Link to="registration"><span>Registration Form</span></Link></li>
    <li className="nav-link"><Link to="students"><span>Students List</span></Link></li>
    <li className="nav-link"><Link to="paystudents"><span>Pay fees</span></Link></li>
  </ul>
  <div className="logout-link">
    <Link to="/login_institute">
      <span>Log Out</span>
    </Link>
  </div>
</nav>
  );
};

export default InstituteDashboard;
